mapper.select:
SELECT COUNT(*)
FROM project.customer
WHERE customer_code = #{customerCode};

SELECT *
FROM project.customer
WHERE customer_no = #{customerNo}
AND hide = 0;

mapper.update:
UPDATE project.customer
SET customer_name = #{dto.customerName},
    customer_tel = #{dto.customerTel},
    ceo = #{dto.ceo}
WHERE customer_no = #{customerNo}
AND hide = 0

UPDATE project.customer
SET hide = 1
WHERE customer_no = #{customerNo}
