mapper.update:
UPDATE project.product
SET hide = 1
WHERE product_no = #{productNo}

mapper.select:
SELECT * FROM project.product WHERE product_code LIKE CONCAT('%', #{productSearchDto.productCode}, '%') AND product_name LIKE CONCAT('%', #{productSearchDto.productName}, '%') AND hide = 0
LIMIT #{productSearchDto.pageSize}
LIMIT #{start}, #{productSearchDto.pageSize}

SELECT COUNT(*)
FROM project.product
WHERE product_code = #{productCode}
