mapper.update:
UPDATE project.product
SET product_name = #{productName},
    product_code = #{productCode},
    standard     = #{standard},
    unit         = #{unit}
WHERE product_no = #{productNo}

mapper.select:
SELECT *
FROM project.product
WHERE product_code LIKE CONCAT('%', #{productSearchDto.productCode}, '%')
AND product_name LIKE CONCAT('%', #{productSearchDto.productName}, '%')


    LIMIT #{productSearchDto.pageSize}


    LIMIT #{start}, #{productSearchDto.pageSize}
